import useFetch from "../api/useFetch";
import { useHistory, useParams } from "react-router";
import { useState } from "react";
import "../scss/editUser.scss";

const EditUser = () => {
    const { userId } = useParams();
    console.log("userIdEdit", userId);
     const { data: people, error, isPending } = useFetch('http://localhost:8000/people/' + userId);

    console.log("peopleName", people.name);
    let [name, setName] = useState(people.name);
    let [contactNo, setContactNo] = useState(people.contactNo);
    let [emailId, setEmailId] = useState(people.emailId);
    let [address, setAddress] = useState(people.address);

    let [university, setUniversity] = useState(people.university);
    let [startYear, setStartYear] = useState(people.startYear);
    let [endYear, setEndYear] = useState(people.endYear);
    let [degree, setDegree] = useState(people.degree);

    let [institution, setInstitution] = useState(people.institution);
    let [workStartYear, setWorkStartYear] = useState('');
    let [workEndYear, setWorkEndYear] = useState('');
    let [title, setTitle] = useState('');

    let [education, setEducation] = useState([]);
    let [workExperience, setExperience] = useState([]);


    const history = useHistory();

    const handleSubmit = (e) => {
        e.preventDefault();

        const user = { name, contactNo, emailId, address, education, workExperience };


        fetch('http://localhost:8000/people'+userId, {
            method: 'PUT',
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(user)
        }).then(() => {
            history.push('/');
        })
    }

    return(
        <div className="edit">
            <h2>Edit User Profile</h2>
            <form onSubmit={handleSubmit}>
                <label>User Name:</label>
                <input
                    type="text"
                    required
                    value={ name }
                    onChange={(e) => setName(e.target.value)} 
                />
                <label>Contact Number:</label>
                <input
                    type="text"
                    required
                    value={ contactNo }
                    onChange={(e) => setContactNo(e.target.value)}
                />
                <label>Email ID:</label>
                <input
                    type="email"
                    required
                    value={ emailId }
                    onChange={(e) => setEmailId(e.target.value)}
                />
                <label>Address:</label>
                <input
                    type="text"
                    required
                    value={ address }
                    onChange={(e) => setAddress(e.target.value)}
                />
                <h4>Education:</h4>
                <label>University:</label>
                <input
                    type="text"
                    required
                    value={ university }
                    onChange={(e) => setUniversity(e.target.value)}
                />
                <label>Start Year:</label>
                <input
                    type="number"
                    placeholder="YYYY"
                    min="1950"
                    max="2021"
                    required
                    value={ startYear }
                    onChange={(e) => setStartYear(e.target.value)}
                />
                <label>End Year:</label>
                <input
                    type="month"
                    required
                    value={ endYear }
                    onChange={(e) => setEndYear(e.target.value)}
                />
                <label>Degree:</label>
                <input
                    type="text"
                    required
                    value={ degree }
                    onChange={(e) => setDegree(e.target.value)}
                />
                <h4>Experience:</h4>
                <label>Institution:</label>
                <input
                    type="text"
                    required
                    value={ institution }
                    onChange={(e) => setInstitution(e.target.value)}
                />
                 <label>Start Year:</label>
                <input
                    type="month"
                    required
                    value={ workStartYear }
                    onChange={(e) => setWorkStartYear(e.target.value)}
                />

                <label>End Year:</label>
                <input
                    type="month"
                    required
                    value={ workEndYear }
                    onChange={(e) => setWorkEndYear(e.target.value)}
                />

                <label>Title:</label>
                <input
                    type="text"
                    required
                    value={ title }
                    onChange={(e) => setTitle(e.target.value)}
                />

                { !isPending && <button className="button-edit">Edit User</button> }
                { isPending && <button className="button-edit" disabled>Editing...</button> }

            </form>
        </div>
    );
}

export default EditUser;
