import { useState } from "react";
import { useHistory } from 'react-router-dom';
import "../scss/create.scss";


const Create = () => {
    const [name, setName] = useState('');
    const [contactNo, setContactNo] = useState('');
    const [emailId, setEmailId] = useState('');
    const [address, setAddress] = useState('');

    const [university, setUniversity] = useState('');
    const [startYear, setStartYear] = useState('');
    const [endYear, setEndYear] = useState('');
    const [degree, setDegree] = useState('');

    const [institution, setInstitution] = useState('');
    const [workStartYear, setWorkStartYear] = useState('');
    const [workEndYear, setWorkEndYear] = useState('');
    const [title, setTitle] = useState('');

    const [education, setEducation] = useState([]);
    const [workExperience, setExperience] = useState([]);

    const [isPending, setIsPending] = useState(false);
    const history = useHistory();

    const [nameErr, setNameErr] = useState({});
    const [contactNoErr, setContactNoErr] = useState({});

    const handleSubmit = (e) => {
        e.preventDefault();

        const isValid = formValidation();

        let tempEducation = education;
        tempEducation.push({institution:university, startYear, endYear, degree});

        let tempExperience = workExperience;
        tempExperience.push({ institution, startYear:workStartYear, endYear:workEndYear, title });

        setEducation(tempEducation);
        setExperience(tempExperience);

        const user = { name, contactNo, emailId, address, education, workExperience };

        setIsPending(true);

        fetch('http://localhost:8000/people', {
            method: 'POST',
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(user)
        }).then(() => {
            setIsPending(false);
            history.push('/');
        })

    }

    const formValidation = () => {
        const nameErr = {};
        const contactNoErr = {};
        let isValid = true;

        if(!name){
            nameErr.mandatory = "Name is required";
            isValid = false;
        }
        if(name.length < 3){
            nameErr.nameShort = "Name is too short";
            isValid = false;
        }

        if(!contactNo){
            contactNoErr.mandatory = "Contact number is required";
            isValid = false;
        }
        else{
            var numberPattern = /^(?:(?:\\+|0{0,2})91(\s*[\\-]\s*)?|[0]?)?[789]\d{9}$/;    
            if (!numberPattern.test(contactNo)) {     
                contactNoErr.invalid = "Invalid Contact Number"; 
                isValid = false;  
            }    

        }

        setNameErr(nameErr);
        setContactNoErr(contactNoErr);
        return isValid;


    }

    return ( 
        <div className="create">
            <h2>Create a new User</h2>
            <form onSubmit={handleSubmit}>  
                <label>User Name:</label>
                <input
                    type="text"
                    required
                    value={ name }
                    onChange={(e) => setName(e.target.value)} 
                />
                {Object.keys(nameErr).map((key) => {
                    return <div style={{color:red}}>{nameErr}</div>
                })}

                <label>Contact Number:</label>
                <input
                    type="text"
                    required
                    value={ contactNo }
                    onChange={(e) => setContactNo(e.target.value)}
                />

                <label>Email ID:</label>
                <input
                    type="email"
                    required
                    value={ emailId }
                    onChange={(e) => setEmailId(e.target.value)}
                />
                <label>Address:</label>
                <input
                    type="text"
                    required
                    value={ address }
                    onChange={(e) => setAddress(e.target.value)}
                />
                <h4>Education:</h4>
                <label>University:</label>
                <input
                    type="text"
                    required
                    value={ university }
                    onChange={(e) => setUniversity(e.target.value)}
                />
                <label>Start Year:</label>
                <input
                    type="date"
                    required
                    value={ startYear }
                    onChange={(e) => setStartYear(e.target.value)}
                />
                <label>End Year:</label>
                <input
                    type="date"
                    required
                    value={ endYear }
                    onChange={(e) => setEndYear(e.target.value)}
                />
                <label>Degree:</label>
                <input
                    type="text"
                    required
                    value={ degree }
                    onChange={(e) => setDegree(e.target.value)}
                />

                <h4>Experience:</h4>
                <label>Institution:</label>
                <input
                    type="text"
                    required
                    value={ institution }
                    onChange={(e) => setInstitution(e.target.value)}
                />

                <label>Start Year:</label>
                <input
                    type="date"
                    required
                    value={ workStartYear }
                    onChange={(e) => setWorkStartYear(e.target.value)}
                />

                <label>End Year:</label>
                <input
                    type="date"
                    required
                    value={ workEndYear }
                    onChange={(e) => setWorkEndYear(e.target.value)}
                />

                <label>Title:</label>
                <input
                    type="text"
                    required
                    value={ title }
                    onChange={(e) => setTitle(e.target.value)}
                />





                { !isPending && <button>Create User</button> }
                { isPending && <button disabled>Creating...</button> }

            </form>
        </div>
     );
}

export default Create;
