import { useState } from "react";
import { useHistory } from 'react-router-dom';
import "../../components/CreateUser/create.scss";

const Create = () => {
    const [name, setName] = useState('');
    const [contactNo, setContactNo] = useState('');
    const [emailId, setEmailId] = useState('');
    const [address, setAddress] = useState('');

    const [university, setUniversity] = useState('');
    const [startYear, setStartYear] = useState('');
    const [endYear, setEndYear] = useState('');
    const [degree, setDegree] = useState('');

    const [institution, setInstitution] = useState('');
    const [workStartYear, setWorkStartYear] = useState('');
    const [workEndYear, setWorkEndYear] = useState('');
    const [title, setTitle] = useState('');

    const [education, setEducation] = useState([]);
    const [workExperience, setExperience] = useState([]);

    const [isPending, setIsPending] = useState(false);
    const history = useHistory();

    const [nameErr, setNameErr] = useState({});
    const [contactNoErr, setContactNoErr] = useState({});
    const [emailIdErr, setEmailIdErr] = useState({});
    const [addressErr, setAddressErr] = useState({});
    const [universityErr, setUniversityErr] = useState({});
    const [degreeErr, setDegreeErr] = useState({});
    const [institutionErr, setInstitutionErr] = useState({});
    const [titleErr, setTitleErr] = useState({});

    const handleSubmit = (e) => {
        e.preventDefault();

        const isValid = formValidation();

        if(isValid){
        let tempEducation = education;
        tempEducation.push({institution:university, startYear, endYear, degree});

        let tempExperience = workExperience;
        tempExperience.push({ institution, startYear:workStartYear, endYear:workEndYear, title });

        setEducation(tempEducation);
        setExperience(tempExperience);

        const user = { name, contactNo, emailId, address, education, workExperience };

        setIsPending(true);

        fetch('http://localhost:8080/people', {
            method: 'POST',
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(user)
        }).then(() => {
            setIsPending(false);
            history.push('/');
        })
    }
    }

    const formValidation = () => {
        const nameErr = {};
        const contactNoErr = {};
        const emailIdErr = {};
        const addressErr = {};
        const universityErr = {};
        const degreeErr = {};
        const institutionErr = {};
        const titleErr = {};
          let isValid = true;

        if(!name){
            nameErr.mandatory = "Name is required";
            isValid = false;
        }
        if(name.length < 3){
            nameErr.nameShort = "Name is too short";
            isValid = false;
        }

        if(!contactNo){
            contactNoErr.mandatory = "Contact number is required";
            isValid = false;
        }
        else{  
            var numberPattern = /^\d{10}$/;  
            if (!numberPattern.test(contactNo)) {     
                contactNoErr.invalid = "Invalid Contact Number"; 
                isValid = false;  
            }    

        }

        if(!emailId){
            emailIdErr.mandatory = "Email ID is required";
            isValid = false;
        }
        else if (!(/^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/.test(emailId))) {       
            emailIdErr.invalid = "Invalid email id.";  
            isValid = false;  
        } 

        if(!address){
            addressErr.mandatory = "Address is required";
            isValid = false;
        }
        if(address.length < 3){
            addressErr.addressShort = "Address is too short";
            isValid = false;
        }

        if(!university){
            universityErr.mandatory = "University is required";
            isValid = false;
        }

        if(!degree){
            degreeErr.mandatory = "Degree is required";
            isValid = false;
        }

        if(!institution){
            institutionErr.mandatory = "Institution is required";
            isValid = false;
        }

        if(!title){
            titleErr.mandatory = "Title is required";
            isValid = false;
        }

        setNameErr(nameErr);
        setContactNoErr(contactNoErr);
        setEmailIdErr(emailIdErr);
        setAddressErr(addressErr);
        setUniversityErr(universityErr);
        setDegreeErr(degreeErr);
        setInstitutionErr(institutionErr);
        setTitleErr(titleErr);

        return isValid;


    }

    return ( 
        <div className="create">
            <h2>Create a new User</h2>
            <form onSubmit={handleSubmit}>  
                <label>User Name:</label>
                <input
                    type="text"
                    required
                    value={ name }
                    onChange={(e) => setName(e.target.value)} 
                />
                {Object.keys(nameErr).map((key) => {
                    return <div style={{color:"red"}}>{nameErr[key]}</div>
                })}

                <label>Contact Number:</label>
                <input
                    type="text"
                    required
                    value={ contactNo }
                    onChange={(e) => setContactNo(e.target.value)}
                />
                 {Object.keys(contactNoErr).map((key) => {
                    return <div style={{color:"red"}}>{contactNoErr[key]}</div>
                })}

                <label>Email ID:</label>
                <input
                    type="email"
                    required
                    value={ emailId }
                    onChange={(e) => setEmailId(e.target.value)}
                />
                {Object.keys(emailIdErr).map((key) => {
                    return <div style={{color:"red"}}>{emailIdErr[key]}</div>
                })}

                <label>Address:</label>
                <input
                    type="text"
                    required
                    value={ address }
                    onChange={(e) => setAddress(e.target.value)}
                />
                {Object.keys(addressErr).map((key) => {
                    return <div style={{color:"red"}}>{addressErr[key]}</div>
                })}

                <h4>Education:</h4>
                <label>University:</label>
                <input
                    type="text"
                    required
                    value={ university }
                    onChange={(e) => setUniversity(e.target.value)}
                />
                {Object.keys(universityErr).map((key) => {
                    return <div style={{color:"red"}}>{universityErr[key]}</div>
                })}

                <label>Start Year:</label>
                <input
                    type="number"
                    placeholder="YYYY"
                    min="1950"
                    max="2021"
                    required
                    value={ startYear }
                    onChange={(e) => setStartYear(e.target.value)}
                />
                <label>End Year:</label>
                <input
                    type="text"
                    placeholder="YYYY or Present"
                    required
                    value={ endYear }
                    onChange={(e) => setEndYear(e.target.value)}
                />
                <label>Degree:</label>
                <input
                    type="text"
                    required
                    value={ degree }
                    onChange={(e) => setDegree(e.target.value)}
                />
                {Object.keys(degreeErr).map((key) => {
                    return <div style={{color:"red"}}>{degreeErr[key]}</div>
                })}

                <h4>Experience:</h4>
                <label>Institution:</label>
                <input
                    type="text"
                    required
                    value={ institution }
                    onChange={(e) => setInstitution(e.target.value)}
                />
                {Object.keys(institutionErr).map((key) => {
                    return <div style={{color:"red"}}>{institutionErr[key]}</div>
                })}

                <label>Start Year:</label>
                <input
                    type="number"
                    placeholder="YYYY"
                    min="1950"
                    max="2021"
                    required
                    value={ workStartYear }
                    onChange={(e) => setWorkStartYear(e.target.value)}
                />

                <label>End Year:</label>
                <input
                    type="text"
                    placeholder="YYYY or Present"
                    required
                    value={ workEndYear }
                    onChange={(e) => setWorkEndYear(e.target.value)}
                />

                <label>Title:</label>
                <input
                    type="text"
                    required
                    value={ title }
                    onChange={(e) => setTitle(e.target.value)}
                />
                {Object.keys(titleErr).map((key) => {
                    return <div style={{color:"red"}}>{titleErr[key]}</div>
                })}

                { !isPending && <button className="button-create">Create User</button> }
                { isPending && <button className="button-create" disabled>Creating...</button> }

            </form>
        </div>
     );
}

export default Create;
