import useFetch from "../api/useFetch";
import { useHistory, useParams } from "react-router";
import { useEffect, useState } from "react";
import "../scss/editUser.scss";

const EditUser = () => {

    console.log("edit user")
    const { userId } = useParams();
    const [people, setPeople] = useState({})
    console.log("userIdEdit", userId);
    const [isPending, setIsPending] = useState(false)
    const [error, setError] = useState(null)
    // const { data: people, error, isPending } = useFetch('http://localhost:8000/people/' + userId);
    console.log("people", people);

    let [name, setName] = useState("");
    let [contactNo, setContactNo] = useState("");
    let [emailId, setEmailId] = useState("");
    let [address, setAddress] = useState("");

    let [university, setUniversity] = useState("");
    let [startYear, setStartYear] = useState("");
    let [endYear, setEndYear] = useState("");
    let [degree, setDegree] = useState("");

    let [institution, setInstitution] = useState("");
    let [workStartYear, setWorkStartYear] = useState('');
    let [workEndYear, setWorkEndYear] = useState('');
    let [title, setTitle] = useState('');

    let [education, setEducation] = useState([]);
    let [workExperience, setExperience] = useState([]);


    const history = useHistory();


    useEffect(()=> {
        async function fetchData() {
            await fetch('http://localhost:8000/people/'+userId)
            .then((res)=>res.json())
            .then((data)=> {
                console.log("data*************", data)
                // set values here
                setPeople(data);
                setName(data.name);
                setContactNo(data.contactNo);
                setEmailId(data.emailId);
                setAddress(data.address);

                setUniversity(data.education[0].institution)
                setStartYear(data.education[0].startYear);
                setEndYear(data.education[0].endYear);
                setDegree(data.education[0].degree);

                setInstitution(data.workExperience[0].institution);
                setWorkEndYear(data.workExperience[0].workStartYear);
                setWorkEndYear(data.workExperience[0].workEndYear);
                setTitle(data.workExperience[0].title);
            })
        }
        fetchData();
    }, [])

    const handleSubmit = (e) => {
        e.preventDefault();

        const user = { name, contactNo, emailId, address, education, workExperience };


        fetch('http://localhost:8000/people'+userId, {
            method: 'PUT',
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(user)
        }).then(() => {
            history.push('/');
        })
    }

    return(
        <div className="edit">
            <h2>Edit User Profile</h2>
            <form onSubmit={handleSubmit}>
                <label>User Name:</label>
                <input
                    type="text"
                    required
                    value={ name }
                    onChange={(e) => setName(e.target.value)} 
                />
                <label>Contact Number:</label>
                <input
                    type="text"
                    required
                    value={ contactNo }
                    onChange={(e) => setContactNo(e.target.value)}
                />
                <label>Email ID:</label>
                <input
                    type="email"
                    required
                    value={ emailId }
                    onChange={(e) => setEmailId(e.target.value)}
                />
                <label>Address:</label>
                <input
                    type="text"
                    required
                    value={ address }
                    onChange={(e) => setAddress(e.target.value)}
                />
                <h4>Education:</h4>
                <label>University:</label>
                <input
                    type="text"
                    required
                    value={ university }
                    onChange={(e) => setUniversity(e.target.value)}
                />
                <label>Start Year:</label>
                <input
                    type="number"
                    placeholder="YYYY"
                    min="1950"
                    max="2021"
                    required
                    value={ startYear }
                    onChange={(e) => setStartYear(e.target.value)}
                />
                <label>End Year:</label>
                <input
                    type="month"
                    required
                    value={ endYear }
                    onChange={(e) => setEndYear(e.target.value)}
                />
                <label>Degree:</label>
                <input
                    type="text"
                    required
                    value={ degree }
                    onChange={(e) => setDegree(e.target.value)}
                />
                <h4>Experience:</h4>
                <label>Institution:</label>
                <input
                    type="text"
                    required
                    value={ institution }
                    onChange={(e) => setInstitution(e.target.value)}
                />
                 <label>Start Year:</label>
                <input
                    type="month"
                    required
                    value={ workStartYear }
                    onChange={(e) => setWorkStartYear(e.target.value)}
                />

                <label>End Year:</label>
                <input
                    type="month"
                    required
                    value={ workEndYear }
                    onChange={(e) => setWorkEndYear(e.target.value)}
                />

                <label>Title:</label>
                <input
                    type="text"
                    required
                    value={ title }
                    onChange={(e) => setTitle(e.target.value)}
                />

                { !isPending && <button className="button-edit">Edit User</button> }
                { isPending && <button className="button-edit" disabled>Editing...</button> }

            </form>
        </div>
    );
}

export default EditUser;
