import { queryAllByTitle } from "@testing-library/dom";
import { useState } from "react";
import { useHistory } from 'react-router-dom';
import "../scss/create.scss";


const Create = () => {
    const [name, setName] = useState('');
    const [contactNo, setContactNo] = useState('');
    const [emailId, setEmailId] = useState('');
    const [address, setAddress] = useState('');

    const [university, setUniversity] = useState('');
    const [startYear, setStartYear] = useState('');
    const [endYear, setEndYear] = useState('');
    const [degree, setDegree] = useState('');

    const [institution, setInstitution] = useState('');
    const [workStartYear, setWorkStartYear] = useState('');
    const [workEndYear, setWorkEndYear] = useState('');
    const [title, setTitle] = useState('');

    const [education, setEducation] = useState([]);
    const [workExperience, setExperience] = useState([]);

    const [isPending, setIsPending] = useState(false);
    const history = useHistory();

    const handleFormValidation = () => {
        let formErrors = {};    
        let formIsValid = true;  

        if(!name){
            formIsValid = false;
            formErrors["nameError"] = "Name is required."; 
        }

        if (!contactNo) {    
            formIsValid = false;    
            formErrors["contactNoError"] = "Contact number is required.";    
        }    
        else {    
            var numberPattern = /^(?:(?:\\+|0{0,2})91(\s*[\\-]\s*)?|[0]?)?[789]\d{9}$/;    
            if (!numberPattern.test(contactNo)) {    
                formIsValid = false;    
                formErrors["contactNoError"] = "Invalid contact number.";    
            }    
        }    

        if (!emailId) {    
            formIsValid = false;    
            formErrors["emailIdError"] = "Email id is required.";    
        }    
        else if (!(/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(emailId))) {    

            formIsValid = false;    
            formErrors["emailIdError"] = "Invalid email id.";    
        }  

        if(!address){
            formIsValid = false;
            formErrors["addressError"] = "Address is required."; 
        }

        this.setState({formErrors: formErrors});
        return formIsValid;
    }

    const handleSubmit = (e) => {
        e.preventDefault();

        if(this.handleFormValidation()){


        let tempEducation = education;
        tempEducation.push({institution:university, startYear, endYear, degree});

        let tempExperience = workExperience;
        tempExperience.push({ institution, startYear:workStartYear, endYear:workEndYear, title });

        setEducation(tempEducation);
        setExperience(tempExperience);

        const user = { name, contactNo, emailId, address, education, workExperience };

        setIsPending(true);

        fetch('http://localhost:8000/people', {
            method: 'POST',
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(user)
        }).then(() => {
            setIsPending(false);
            history.push('/');
        })
        }
    }
    const {nameError} = formErrors;
    return ( 
        <div className="create">
            <h2>Create a new User</h2>
            <form onSubmit={handleSubmit}>  
                <label>User Name:</label>
                <input
                    type="text"
                    required
                    value={ name }
                    onChange={(e) => setName(e.target.value)}

                    className={nameError ? ' showError' : ''} />
                            {nameError &&    
                                <div style={{ color: "red", paddingBottom: 10 }}>{nameError}</div>    
                            }    

                <label>Contact Number:</label>
                <input
                    type="text"
                    required
                    value={ contactNo }
                    onChange={(e) => setContactNo(e.target.value)}
                />
                <label>Email ID:</label>
                <input
                    type="email"
                    required
                    value={ emailId }
                    onChange={(e) => setEmailId(e.target.value)}
                />
                <label>Address:</label>
                <input
                    type="text"
                    required
                    value={ address }
                    onChange={(e) => setAddress(e.target.value)}
                />
                <h4>Education:</h4>
                <label>University:</label>
                <input
                    type="text"
                    required
                    value={ university }
                    onChange={(e) => setUniversity(e.target.value)}
                />
                <label>Start Year:</label>
                <input
                    type="date"
                    required
                    value={ startYear }
                    onChange={(e) => setStartYear(e.target.value)}
                />
                <label>End Year:</label>
                <input
                    type="date"
                    required
                    value={ endYear }
                    onChange={(e) => setEndYear(e.target.value)}
                />
                <label>Degree:</label>
                <input
                    type="text"
                    required
                    value={ degree }
                    onChange={(e) => setDegree(e.target.value)}
                />

                <h4>Experience:</h4>
                <label>Institution:</label>
                <input
                    type="text"
                    required
                    value={ institution }
                    onChange={(e) => setInstitution(e.target.value)}
                />

                <label>Start Year:</label>
                <input
                    type="date"
                    required
                    value={ workStartYear }
                    onChange={(e) => setWorkStartYear(e.target.value)}
                />

                <label>End Year:</label>
                <input
                    type="date"
                    required
                    value={ workEndYear }
                    onChange={(e) => setWorkEndYear(e.target.value)}
                />

                <label>Title:</label>
                <input
                    type="text"
                    required
                    value={ title }
                    onChange={(e) => setTitle(e.target.value)}
                />





                { !isPending && <button>Create User</button> }
                { isPending && <button disabled>Creating...</button> }

            </form>
        </div>
     );
}

export default Create;
