import React from "react";
import "../scss/userList.scss";
import {getRandomColor, createImageFromInitials} from "../components/ProfilePicture";

const UserList = ({people, selectedUser}) => {
    let alphabetOrder = {}
    console.log("people", people);

    people.forEach(element => {
        if(alphabetOrder[element.name[0]]){
            alphabetOrder[element.name[0]].push(element)
        }else {
            alphabetOrder[element.name[0]] = [element]
        }
    });

    const selectUserData = (user) => {
        selectedUser(user)
    }
        return ( 
        <div className="user-list">
            {Object.keys(alphabetOrder).map(alphabets=> 
                <>
                <details>
                <summary>{alphabets.toUpperCase()}</summary>
                {alphabetOrder[alphabets].map(people=> {
                    return (<div onClick={()=> selectUserData(people)} className="user-preview" key={people.id}>

                        <div className="profileImage">{createImageFromInitials(people.name, getRandomColor())}
                        <span className="nameDisplay">{people.name}</span>
                       </div>
                    </div>)})
                }</details>
                </>

            )}
        </div>
     );
}

export default UserList;
