import useFetch from "../api/useFetch";
import { useHistory } from "react-router";
import { useState } from "react";

const EditUser = ({userId}) => {
    console.log("peopleEdit", userId);
     const { data: people, error, isPending } = useFetch('http://localhost:8000/people/' + userId);
     const history = useHistory();

    let [name, setName] = useState(people.name);
    let [contactNo, setContactNo] = useState(people.contactNo);
    let [emailId, setEmailId] = useState(people.emailId);
    let [address, setAddress] = useState(people.address);
    let [university, setUniversity] = useState(people.university);
    let [startYear, setStartYear] = useState(people.startYear);
    let [endYear, setEndYear] = useState(people.endYear);

    return(
        <div>
            <h2>Edit User Profile</h2>
            <form>
                <label>User Name:</label>
                <input
                    type="text"
                    required
                    value={ name }
                    onChange={(e) => setName(e.target.value)} 
                />
                <label>Contact Number:</label>
                <input
                    type="text"
                    required
                    value={ contactNo }
                    onChange={(e) => setContactNo(e.target.value)}
                />
                <label>Email ID:</label>
                <input
                    type="email"
                    required
                    value={ emailId }
                    onChange={(e) => setEmailId(e.target.value)}
                />
                <label>Address:</label>
                <input
                    type="text"
                    required
                    value={ address }
                    onChange={(e) => setAddress(e.target.value)}
                />
                <h4>Education:</h4>
                <label>University:</label>
                <input
                    type="text"
                    required
                    value={ university }
                    onChange={(e) => setUniversity(e.target.value)}
                />
                <label>Start Year:</label>
                <input
                    type="number"
                    placeholder="YYYY"
                    min="1950"
                    max="2021"
                    required
                    value={ startYear }
                    onChange={(e) => setStartYear(e.target.value)}
                />
                <label>End Year:</label>
                <input
                    type="month"
                    required
                    value={ endYear }
                    onChange={(e) => setEndYear(e.target.value)}
                />
                <label>Degree:</label>
                <input
                    type="text"
                    required
                    value={ degree }
                    onChange={(e) => setDegree(e.target.value)}
                />
            </form>
        </div>
    );
}

export default EditUser;
