import { useHistory } from "react-router";
import React, { useState } from "react";
import useFetch from "../api/useFetch";
import "../scss/userDetails.scss";
import { FaUserEdit } from 'react-icons/fa';
import { MdDelete } from 'react-icons/md';
import Modal from "react-modal";
import avatar from "../assets/avatar.png";
import ReactTooltip from "react-tooltip";
import { Link } from 'react-router-dom';
import EditUser from "./EditUser";


const UserDetails = ({userId}) => {

    const { data: people, error, isPending } = useFetch('http://localhost:8000/people/' + userId);
     const history = useHistory();

    const renderEducation = (data) => {
        return(<div>
            {data.map((edu, index)=> {
                return <>
                <section className="education-details">
                <div className="year">{`${edu.startYear}-${edu.endYear}`}</div>
                <div className="college-course">
                    <h4>{edu.institution}</h4>
                    <p>{edu.degree}</p>
                </div>
                </section>
                </>
            })}
        </div>)
    }

    const renderExperience = (data) => {
        return(<div>
            {data.map((edu, index)=> {
                return <>
                <section className="experience-details">
                <div className="year">{`${edu.startYear}-${edu.endYear? edu.endYear:'Present'}`}</div>
                <div className="company-details">
                    <h4>{edu.institution}</h4>
                    <p>{edu.title}</p>
                </div>
                </section>
                </>
            })}
        </div>)
    }

    Modal.setAppElement("#root");
    const [isOpen, setIsOpen] = useState(false);

    function toggleModal() {
        setIsOpen(!isOpen);
    }

    const handleDelete = () => {
        fetch('http://localhost:8000/people/' + userId, {
            method: 'DELETE'
        }).then(() => {
            setIsOpen(!isOpen);
            history.push('/');
        })
}

    return ( 
        <div className="user-details">
            { isPending && <div>Loading...</div> }
            { error && <div>{ error }</div> }
            { people && (
               <><section className="personal-details">
                    <article className="profile-picture">
                        <img className="avatar" 
                            src={avatar} alt={"Profile picture"}
                        />


                    </article>
                    <article className="personal-data">
                        <h2>{ people.name }</h2>
                        <p>{ people.contactNo }</p>
                        <p>{ people.emailId }</p>
                        <p>{ people.address }</p>
                    </article>
                    <article>

                        <Link className="editButton" to="/edit" data-tip data-for="editTip" onClick={handleEdit}><FaUserEdit /></Link> 
                        <ReactTooltip className="edit" id="editTip" place="bottom" effect="solid">
                            Edit User
                        </ReactTooltip> 
                        <button className="deleteButton" data-tip data-for="deleteTip" onClick={toggleModal}><MdDelete /></button> 
                        <ReactTooltip className="delete" id="deleteTip" place="bottom" effect="solid">
                            Delete User
                        </ReactTooltip> 
                    </article>
                    <Modal
                            isOpen={isOpen}
                            onRequestClose={toggleModal}
                            contentLabel="My dialog"
                            className="deleteModal"
                            overlayClassName="deleteOverlay"
                            closeTimeoutMS={500}
                    >
                            <div>Are you sure you want to delete the User?</div>
                            <button onClick={handleDelete}>Yes</button>
                            <button onClick={toggleModal}>No</button>

                    </Modal>
                </section>
                <section className="education">
                    <h2>EDUCATION</h2>

                    {renderEducation(people.education)}


                </section>
                <section className="experience">
                    <h2>EXPERIENCE</h2>
                    {renderExperience(people.workExperience)}
                </section>
                </>
            )}
        </div>
     );
}

export default UserDetails;
