import { queryAllByTitle } from "@testing-library/dom";
import { useState } from "react";
import { useHistory } from 'react-router-dom';
import "../scss/create.scss";


const Create = () => {
    const [name, setName] = useState('');
    const [contactNo, setContactNo] = useState('');
    const [emailId, setEmailId] = useState('');
    const [address, setAddress] = useState('');
    const [university, setUniversity] = useState('');
    const [startYear, setStartYear] = useState('');
    const [endYear, setEndYear] = useState('');
    const [degree, setDegree] = useState('');

    const [education, setEducation] = useState([]);
    const [experience, setExperience] = useState([]);

    const [isPending, setIsPending] = useState(false);
    const history = useHistory();

    const handleSubmit = (e) => {
        e.preventDefault();
        const user = { name, contactNo, emailId, address, education, experience };
        const education = { university, startYear, endYear, degree };
        const experience = { institution, workStartYear, workEndYear, title };

        setIsPending(true);

        fetch('http://localhost:8000/people', {
            method: 'POST',
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(user, education, experience)
        }).then(() => {
            setIsPending(false);
            history.push('/');
        })
    }

    return ( 
        <div className="create">
            <h2>Create a new User</h2>
            <form onSubmit={handleSubmit}>  
                <label>User Name:</label>
                <input
                    type="text"
                    required
                    value={ name }
                    onChange={(e) => setName(e.target.value)}
                />
                <label>Contact Number:</label>
                <input
                    type="text"
                    required
                    value={ contactNo }
                    onChange={(e) => setContactNo(e.target.value)}
                />
                <label>Email ID:</label>
                <input
                    type="email"
                    required
                    value={ emailId }
                    onChange={(e) => setEmailId(e.target.value)}
                />
                <label>Address:</label>
                <input
                    type="text"
                    required
                    value={ address }
                    onChange={(e) => setAddress(e.target.value)}
                />
                <h4>Education:</h4>
                <label>University:</label>
                <input
                    type="text"
                    required
                    value={ university }
                    onChange={(e) => setUniversity(e.target.value)}
                />
                <label>Start Year:</label>
                <input
                    type="date"
                    required
                    value={ startYear }
                    onChange={(e) => setStartYear(e.target.value)}
                />
                <label>End Year:</label>
                <input
                    type="date"
                    required
                    value={ endYear }
                    onChange={(e) => setEndYear(e.target.value)}
                />
                <label>Degree:</label>
                <input
                    type="text"
                    required
                    value={ degree }
                    onChange={(e) => setDegree(e.target.value)}
                />

                <h4>Experience:</h4>
                <label>Institution:</label>
                <input
                    type="text"
                    required
                    value={ institution }
                    onChange={(e) => setExperience(e.target.value)}
                />

                <label>Start Year:</label>
                <input
                    type="date"
                    required
                    value={ workStartYear }
                    onChange={(e) => setExperience(e.target.value)}
                />

                <label>End Year:</label>
                <input
                    type="date"
                    required
                    value={ workEndYear }
                    onChange={(e) => setExperience(e.target.value)}
                />

                <label>Title:</label>
                <input
                    type="text"
                    required
                    value={ title }
                    onChange={(e) => setExperience(e.target.value)}
                />





                { !isPending && <button>Create User</button> }
                { isPending && <button disabled>Creating...</button> }

            </form>
        </div>
     );
}

export default Create;
